      console.log(`‚ö° ${agent.name} (${agent.model}) thinking...`);

      const response = await agent.provider.chat(
        messages,
        agent.systemPrompt
      );

      const duration = Date.now() - startTime;
      console.log(`‚úì ${agent.name} responded in ${(duration / 1000).toFixed(1)}s`);

      return {
        agentName: agent.name,
        model: agent.model,
        content: response.text || '',
        tokens: response.usage ? {
          input: response.usage.input_tokens || 0,
          output: response.usage.output_tokens || 0,
          total: (response.usage.input_tokens || 0) + (response.usage.output_tokens || 0)
        } : { input: 0, output: 0, total: 0 },
        duration_ms: duration
      };

    } catch (error: any) {
      // Graceful degradation: Return error response but don't fail entire consultation
      const duration = Date.now() - startTime;
      console.warn(`‚ö†Ô∏è  Agent ${agent.name} failed: ${error.message}`);

      return {
